import React, { useEffect, useState } from "react";

function MintLedger(props) {
  const ledger = props.mintHistory.map((prop) => {
    let time = new Date(prop.time * 1000)
    const tokenUrl = "http://localhost:3000/token?address="
    return (
      <div>
        <p>Time stamp: {time.toUTCString()}</p>
        <p>Address:  <a href = {tokenUrl.concat(prop.tokenAddress)}>{prop.tokenAddress}</a></p>
      </div>
    )
  });

  useEffect(() => {
    async function fetchMintHistory() {
      const tokenMinterAddress = '0x19A5A6878e66e5E12F04114ca5ef8e94919Ac9C8';
      const tokenMinterContract = await new web3.eth.Contract(TokenMinter.abi, tokenMinterAddress);
      return await tokenMinterContract.methods.getMintHistory().call();
    }
    if (web3 !== null) {
      fetchMintHistory().then(res => {setMintHistory(res)})
    }
  },[props.mintHistory])

  return (
    <div>
      <h1>
        Ledger
      </h1>
      {props.mintHistory.length?<p>Currently loaded token minter's ledger</p>:<p>Currently loading token minter's ledger</p>}
    </div>
  );
}

export default MintLedger;
