import React, { Component } from "react";
import Web3 from 'web3';
import logo from './logo.svg';
import TokenMinter from './contracts/TokenMinter.json'
import './App.css';

class App extends Component {
  constructor() {
    super();
    if (window.ethereum) {
      //get user's metamask account
      web3 = new Web3(window.ethereum);
      ethereum.request({ method: 'eth_requestAccounts' }).then((accounts) => {
        this.state.account = accounts[0]
        console.log(account)
      })

      window.ethereum.on('accountsChanged', function (accounts) {
        this.state.account = accounts[0]
        console.log(account)
      })
    } else {
      //connect to the default provider
      web3 = new Web3(new Web3.providers.HttpProvider("https://rinkeby.infura.io/v3/9b11910966d3430e9846e504d5847593"))
    }
    const tokenMinterAddress = '0x19A5A6878e66e5E12F04114ca5ef8e94919Ac9C8'
    const tokenMinterContract = new web3.eth
      .Contract(TokenMinter.abi, tokenMinterAddress)
  }

  mintToken() {
    console.log('minted')
  }

  renderMinterDiv() {
    minter.append("<div class=\"minterdiv\">")
    minter.append("<p>Token's owner: <input id=\"owner\" type=\"text\" /></p>")
    minter.append("<p>Token's name: <input id=\"name\" type=\"text\" /> </p>")
    minter.append("<p><button id=\"mint_token\" type=\"button\">Mint</button></p>")
    minter.append("</div>")
    return (
      <div class="minterdiv">
        <p>Token's owner: <TextField value={this.state.textFieldValue} onChange={this._handleTextFieldChange} /></p>
        <p>Token's name: <TextField value={this.state.textFieldValue} onChange={this._handleTextFieldChange} /></p>
        <p><button onClick={() => this.mintToken()}>Mint</button></p>
      </div>
    );
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          {this.renderMinterDiv()}
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>
      </div>
    );
  }
}

export default App;
