import React, { Component } from "react";
import Web3 from 'web3';
import logo from './logo.svg';
import TokenMinter from './contracts/TokenMinter.json'
import './App.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      web3: "",
      account: "",
      ownerTextFieldValue: "",
      nameTextFieldValue: ""
    }
    if (window.ethereum) {
      //get user's metamask account
      this.state.web3 = new Web3(window.ethereum);
      window.ethereum.request({ method: 'eth_requestAccounts' }).then((accounts) => {
        this.state.account = accounts[0]
        console.log(this.state.account)
      })

      this.handleAccountChange.bind(this)
      window.ethereum.on('accountsChanged', handleAccountChange)
    } else {
      //connect to the default provider
      this.state.web3 = new Web3(new Web3.providers.HttpProvider("https://rinkeby.infura.io/v3/9b11910966d3430e9846e504d5847593"))
    }
    const tokenMinterAddress = '0x19A5A6878e66e5E12F04114ca5ef8e94919Ac9C8'
    const tokenMinterContract = new this.state.web3.eth
      .Contract(TokenMinter.abi, tokenMinterAddress)
  }

  handleAccountChange = (...args) => {
    // you can console to see the args
    const accounts = args[0] ;
    // if no accounts that means we are not connected
    if (accounts.length === 0) {
      console.log("Please connect to metamask");
      // our old data is not current connected account
      // currentAccount account that you already fetched and assume you stored it in useState
    } else if (accounts[0] !== currentAccount) {
      // if account changed you should update the currentAccount so you return the updated the data
      // assuming you have [currentAccount,setCurrentAccount]=useState
      // however you are tracking the state currentAccount, you have to update it. in case of redux you have to dispatch update action etc
      setCurrentAccount(accounts[0])
    }
  };

  mintToken() {
    console.log('minted')
  }

  handleOwnerTextFieldChange(e) {
    this.setState({
      ownerTextFieldValue: e.target.value
    });
  }

  handleNameTextFieldChange(e) {
    this.setState({
      nameTextFieldValue: e.target.value
    });
  }

  renderMinterDiv() {
    console.log("where?" + this.state.account)
    if (this.state.account === "0xf61b13cd9b7e6cc7e3609f604232953ca8614bbc") {
      return (
        <div class="minterdiv">
          <p>Token's owner: <input id="owner" type="text" onChange={this.handleOwnerTextFieldChange}/></p>
          <p>Token's name: <input id="name" type="text" onChange={this.handleNameTextFieldChange}/></p>
          <p><button onClick={() => this.mintToken()}>Mint</button></p>
        </div>
      );
    } else {
      return ;
    }
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          {this.renderMinterDiv()}
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>
      </div>
    );
  }
}

export default App;
