import React, { Component } from "react";
import Web3 from 'web3';
import TokenMinter from './contracts/TokenMinter.json'
import MintLedger from './MintLedger'
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      web3: null,
      account: null,
      tokenMinterContract: null,
      minterLedger: null,
      ownerTextFieldValue: null,
      nameTextFieldValue: null
    }

    if (window.ethereum) {
      //get user's metamask account
      this.state.web3 = new Web3(window.ethereum);
      window.ethereum.request({ method: 'eth_requestAccounts' }).then((accounts) => {
        this.setState({
          account: accounts[0]
        })
      })

      window.ethereum.on('accountsChanged', (accounts) => {
        this.setState({
          account: accounts[0]
        })
      })
    } else {
      //connect to the default provider
      this.state.web3 = new Web3(new Web3.providers.HttpProvider("https://rinkeby.infura.io/v3/9b11910966d3430e9846e504d5847593"))
    }

    const tokenMinterAddress = '0x19A5A6878e66e5E12F04114ca5ef8e94919Ac9C8'
    this.state.tokenMinterContract = new this.state.web3.eth
      .Contract(TokenMinter.abi, tokenMinterAddress)
    this.state.minterLedger = this.state.tokenMinterContract.methods.getMintHistory().call()
  }

  componentDidMount () {
    this.state.tokenMinterContract.methods.getMintHistory().call()
      .then(resp => this.setState({minterLedger: resp}))
  }

  mintToken = async() => {
    let totalSupply = await this.state.tokenMinterContract.methods.getTotalSupply().call()
    console.log(totalSupply)
  }

  handleOwnerTextFieldChange = (e) => {
    this.setState({
      ownerTextFieldValue: e.target.value
    })
  }

  handleNameTextFieldChange = (e) => {
    this.setState({
      nameTextFieldValue: e.target.value
    })
  }

  renderMinterDiv() {
    if (this.state.account === "0xf61b13cd9b7e6cc7e3609f604232953ca8614bbc") {
      return (
        <div class="minterdiv">
          <p>Token's owner: <input id="owner" type="text" onChange={this.handleOwnerTextFieldChange}/></p>
          <p>Token's name: <input id="name" type="text" onChange={this.handleNameTextFieldChange}/></p>
          <p><button onClick={() => this.mintToken()}>Mint</button></p>
        </div>
      );
    } else {
      return ;
    }
  }

  renderMintLedger() {
    if (this.state.minterLedger != null) {
      return (
        <MintLedger
          mintHistory={this.state.minterLedger}
        />
      );
    }
  }

  render() {
    return (
      <div className="App">
        <p>
          TokenMinter
        </p>
        {this.renderMinterDiv()}
        {this.renderMintLedger()}
      </div>
    );
  }
}

export default App;
