import React, { Component } from "react";
import Web3 from 'web3';
import logo from './logo.svg';
import TokenMinter from './contracts/TokenMinter.json'
import './App.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      account: "",
      ownerTextFieldValue: "",
      nameTextFieldValue: ""
    }
  }

  mintToken() {
    console.log('minted')
  }

  handleOwnerTextFieldChange(e) {
    this.setState({
      ownerTextFieldValue: e.target.value
    });
  }

  handleNameTextFieldChange(e) {
    this.setState({
      nameTextFieldValue: e.target.value
    });
  }

  renderMinterDiv() {
    if (this.state.account === '0xf61b13cd9b7e6cc7e3609f604232953ca8614bbc') {
      return (
        <div class="minterdiv">
          <p>Token's owner: <input id="owner" type="text" onChange={this.handleOwnerTextFieldChange}/></p>
          <p>Token's name: <input id="name" type="text" onChange={this.handleNameTextFieldChange}/></p>
          <p><button onClick={() => this.mintToken()}>Mint</button></p>
        </div>
      );
    } else {
      return ;
    }
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          {this.renderMinterDiv()}
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>
      </div>
    );
  }
}

export default App;
